      const path = await import('path');

      const backupDir = './data/bjj_backup';
      await fs.mkdir(backupDir, { recursive: true });

      // Backup all data arrays
      const backupData = {
        users: this.users,
        classes: this.classes,
        notes: this.notes,
        videos: this.videos,
        drawings: this.drawings,
        belts: this.belts,
