  --secondary: hsl(200, 80%, 75%);
  --secondary-foreground: hsl(260, 80%, 15%);
  --accent: hsl(45, 90%, 75%);
  --accent-foreground: hsl(260, 80%, 15%);
  --destructive: hsl(15, 90%, 65%);
