import { Purchases } from '@revenuecat/purchases-js';
import type { CustomerInfo } from '@revenuecat/purchases-js';
import { getPlatform } from './platform';

/**
 * RevenueCat service for handling multi-platform subscriptions
 * - Web: Uses Stripe via RevenueCat Web Billing
 * - Android/iOS: Use native SDKs (@revenuecat/purchases-capacitor)
 * 
 * Note: This is the WEB SDK only. For native platforms, use Capacitor plugin.
 */
class RevenueCatService {
  private purchasesInstance: any = null;

  /**
   * Initialize RevenueCat SDK (Web only)
   * Call this once when the app starts
   */
  async initialize(userId: string) {
    if (this.purchasesInstance) {
      return;
    }

    const apiKey = import.meta.env.VITE_REVENUECAT_PUBLIC_SDK_KEY;

    if (!apiKey) {
      console.warn('RevenueCat SDK key not found. Subscriptions will not work.');
      return;
    }

    try {
      this.purchasesInstance = Purchases.configure({
        apiKey,
        appUserId: userId,
      });

      console.log('‚úÖ RevenueCat initialized for user:', userId);
    } catch (error) {
      console.error('‚ùå Failed to initialize RevenueCat:', error);
    }
  }

  /**
   * Get available subscription offerings
   */
  async getOfferings() {
    if (!this.purchasesInstance) {
      throw new Error('RevenueCat not initialized');
    }

    try {
      const offerings = await this.purchasesInstance.getOfferings();
      return offerings;
    } catch (error) {
      console.error('Error fetching offerings:', error);
      return null;
    }
  }

  /**
   * Purchase a subscription package
   */
  async purchasePackage(pkg: any, email?: string): Promise<CustomerInfo> {
    if (!this.purchasesInstance) {
      throw new Error('RevenueCat not initialized');
    }

    try {
      const customerInfo = await this.purchasesInstance.purchase({
        packageToPurchase: pkg,
        email,
      });
      return customerInfo;
    } catch (error: any) {
      if (error.errorCode === 'UserCancelledError') {
        throw new Error('Purchase cancelled');
      }
      throw error;
    }
  }

  /**
   * Get current customer info (subscription status)
   */
  async getCustomerInfo(): Promise<CustomerInfo | null> {
    if (!this.purchasesInstance) {
      return null;
    }

    try {
      const customerInfo = await this.purchasesInstance.getCustomerInfo();
      return customerInfo;
    } catch (error) {
      console.error('Error getting customer info:', error);
      return null;
    }
  }

  /**
   * Check if user has active subscription
   */
  async hasActiveSubscription(): Promise<boolean> {
    const customerInfo = await this.getCustomerInfo();

    if (!customerInfo) {
      return false;
    }

    return Object.keys(customerInfo.entitlements.active).length > 0;
  }

  /**
   * Get active subscription tier (if any)
   */
  async getSubscriptionTier(): Promise<'free' | 'enthusiast' | 'gym_pro'> {
    const customerInfo = await this.getCustomerInfo();

    if (!customerInfo) {
      return 'free';
    }

    // Check for Gym Pro entitlement first (higher tier)
    if (customerInfo.entitlements.active['gym_pro']) {
      return 'gym_pro';
    }

    // Then check for Enthusiast
    if (customerInfo.entitlements.active['enthusiast']) {
      return 'enthusiast';
    }

    return 'free';
  }

  /**
   * Switch to a different user ID
   */
  async changeUser(newUserId: string) {
    if (!this.purchasesInstance) {
      return;
    }

    try {
      await this.purchasesInstance.changeAppUserId(newUserId);
    } catch (error) {
      console.error('Error changing user:', error);
    }
  }
}

export const revenueCatService = new RevenueCatService();

client/src/pages/Subscribe.tsx.content:
import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Check, Crown, Zap, Shield, Smartphone, ExternalLink } from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useAuth } from '@/contexts/AuthContext';
import { useToast } from '@/hooks/use-toast';
import { getPlatform, isWebPlatform } from '@/lib/platform';

export default function Subscribe() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [loadingTier, setLoadingTier] = useState<string | null>(null);
  const platform = getPlatform();
  const isWeb = isWebPlatform();

  const { data: subscriptionStatus } = useQuery<{ tier: string; status: string }>({
    queryKey: ['/api/stripe/subscription-status'],
    enabled: !!user && isWeb, // Only fetch Stripe status on web
  });

  const currentTier = subscriptionStatus?.tier || 'free';

  const openAppStore = () => {
    const appId = 'com.jitsjournal.app';

    if (platform === 'android') {
      // Open Play Store app or web page
      window.location.href = `market://details?id=${appId}`;

      // Fallback to web if Play Store app not available
      setTimeout(() => {
        window.location.href = `https://play.google.com/store/apps/details?id=${appId}`;
      }, 500);
    } else if (platform === 'ios') {
      // When iOS app is published, we'll add the App Store ID
      // window.location.href = `itms-apps://apps.apple.com/app/id<YOUR_APP_ID>`;
      toast({
        title: 'Coming Soon',
        description: 'iOS App Store version coming soon!',
      });
    }
  };

  const handleSubscribe = (tier: string) => {
    if (isWeb) {
      toast({
        title: 'Subscribe via App Store',
        description: 'Download the app from Google Play Store or Apple App Store to unlock premium features.',
        duration: 5000,
      });
      return;
    }

    // Open the app store subscription page
    setLoadingTier(tier);

    toast({
      title: 'Opening Store...',
      description: `Subscribe to ${tier === 'enthusiast' ? 'BJJ Enthusiast' : 'Gym Pro'} in the ${platform === 'android' ? 'Play' : 'App'} Store`,
    });

    setTimeout(() => {
      openAppStore();
      setLoadingTier(null);
    }, 500);
  };

  const tiers = [
    {
      name: 'Free',
      price: '$0',
      tier: 'free',
      description: 'Get started with BJJ tracking',
      features: [
        'View community notes',
        'View gym notes (with code)',
        'Log up to 3 classes',
        'Create up to 3 notes',
        'No note sharing',
      ],
      icon: Shield,
      color: 'text-gray-600',
      bgColor: 'bg-gray-50',
      borderColor: 'border-gray-200',
      isCurrent: currentTier === 'free',
    },
    {
      name: 'BJJ Enthusiast',
      price: '$9.99',
      tier: 'enthusiast',
      description: 'For dedicated practitioners',
      priceId: import.meta.env.VITE_STRIPE_ENTHUSIAST_PRICE_ID || import.meta.env.STRIPE_ENTHUSIAST_PRICE_ID,
      features: [
        'Unlimited class logs',
        'Unlimited notes',
        'Share to community: 1x/week',
        'View all gym notes',
        'Priority support',
      ],
      icon: Zap,
      color: 'text-blue-600',
      bgColor: 'bg-blue-50',
      borderColor: 'border-blue-200',
      isCurrent: currentTier === 'enthusiast',
      popular: true,
    },
    {
      name: 'Gym Pro',
      price: '$19.99',
      tier: 'gym_pro',
      description: 'For gym owners & coaches',
      priceId: import.meta.env.VITE_STRIPE_GYM_PRO_PRICE_ID || import.meta.env.STRIPE_GYM_PRO_PRICE_ID,
      features: [
        'Everything in Enthusiast',
        'Share to community: 10x/week',
        'Share to gym: Unlimited',
        'Admin approval required',
        'Premium support',
      ],
      icon: Crown,
      color: 'text-purple-600',
      bgColor: 'bg-purple-50',
      borderColor: 'border-purple-200',
      isCurrent: currentTier === 'gym_pro',
    },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-white dark:from-gray-900 dark:to-gray-800 py-12 px-4">
      <div className="max-w-7xl mx-auto">
        {/* App Store Notice for Mobile */}
        {!isWeb && (
          <div className="mb-8 bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-6">
            <div className="flex items-start gap-3">
              <ExternalLink className="w-6 h-6 text-blue-600 flex-shrink-0 mt-1" />
              <div>
                <h3 className="font-semibold text-gray-900 dark:text-white mb-2">
                  Subscribe via {platform === 'android' ? 'Google Play Store' : 'Apple App Store'}
                </h3>
                <p className="text-sm text-gray-600 dark:text-gray-400 mb-3">
                  Click "Get Started" on any plan below to open the {platform === 'android' ? 'Play' : 'App'} Store where you can complete your subscription.
                </p>
                <p className="text-sm text-gray-500 dark:text-gray-500 italic">
                  üí° Your subscription will sync automatically after purchase!
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 dark:text-white mb-4">
            Choose Your Plan
          </h1>
          <p className="text-lg text-gray-600 dark:text-gray-400">
            Track your BJJ journey with the right plan for you
          </p>
        </div>

        {/* Pricing Cards */}
        <div className="grid md:grid-cols-3 gap-8 mb-16">
          {tiers.map((tier) => {
            const Icon = tier.icon;
            return (
              <Card
                key={tier.tier}
                className={`relative ${tier.popular ? 'ring-2 ring-blue-500 shadow-xl' : ''} ${
                  tier.isCurrent ? 'border-green-500' : tier.borderColor
                }`}
                data-testid={`tier-card-${tier.tier}`}
              >
                {tier.popular && (
                  <div className="absolute -top-4 left-1/2 -translate-x-1/2 bg-blue-500 text-white px-4 py-1 rounded-full text-sm font-semibold">
                    Most Popular
                  </div>
                )}

                {tier.isCurrent && (
                  <div className="absolute -top-4 right-4 bg-green-500 text-white px-4 py-1 rounded-full text-sm font-semibold">
                    Current Plan
                  </div>
                )}

                <CardHeader>
                  <div className={`w-12 h-12 rounded-lg ${tier.bgColor} flex items-center justify-center mb-4`}>
                    <Icon className={`w-6 h-6 ${tier.color}`} />
